/**
 * Search line on demand.
 * Loading the next pool of models.
 */
$(document).ready(function(){$('body').on('click','.btn.search-submit',function(){$("#model-grid").yiiGridView("applyFilter")});$('body').on('click','.btn.load-more',function(){var that=$(this);var offset=that.attr('data-offset');var model_index=$('.model-index');var model_grid=model_index.find('#model-grid');var filter=model_grid.find('.filters');var spinner=$('.model-spinner');spinner.show();$.ajax({url:load_more_url,data:(filter.find('.form-control').serialize()+'&offset='+offset),success:function(response){if (response.count){model_grid.append(response.content);that.attr('data-offset',offset+response.count)}else that.attr('disabled','disabled');spinner.hide()}})})});
